<!DOCTYPE html><html lang="es"><head>    <meta charset="UTF-8">    <title>Listado de Proveedores</title>    <link rel="stylesheet" href="../../../public/css/tabla.css"></head><body>    <h1>Listado de Proveedores</h1>    <table border="1">    <thead>        <tr>            <th>ID Proveedor</th>            <th>Nombre</th>            <th>Dirección</th>            <th>Email</th>            <th>Teléfono</th>            <th>Producto Suministrado</th>            <th>Días de Entrega</th>            <th>Tipo de Proveedor</th>            <th>Razón Social</th>            <th>Número de Empleados</th>        </tr>    </thead>    <tbody>        <?php foreach ($providers as $provider): ?>            <tr>                <td><?= htmlspecialchars($provider['provider_id']) ?></td>                <td><?= htmlspecialchars($provider['name']) ?></td>                <td><?= htmlspecialchars($provider['address']) ?></td>                <td><?= htmlspecialchars($provider['email']) ?></td>                <td><?= htmlspecialchars($provider['phone_number']) ?></td>                <td><?= htmlspecialchars($provider['product_supplied']) ?></td>                <td><?php                 if (is_string($provider['delivery_days'])) {                $provider['delivery_days'] = json_decode($provider['delivery_days'], true);                }                if (is_array($provider['delivery_days'])) {                echo htmlspecialchars(implode(", ", $provider['delivery_days']));                } else {                echo 'N/A';                }                ?></td>                <td><?= htmlspecialchars($provider['provider_type']) ?></td>                <td><?= $provider['provider_type'] === 'empresa' ? htmlspecialchars($provider['corporate_reason']) : 'N/A' ?> </td>                <td><?= $provider['provider_type'] === 'empresa' ? htmlspecialchars($provider['company_workers']) : 'N/A' ?></td>            </tr>        <?php endforeach; ?>    </tbody></table></body></html>